import React, { ReactNode } from "react";

type ButtonProps = {
  label: string;
  leftIcon?: ReactNode;
  rightIcon?: ReactNode;
  loading?: boolean;
  disabled?: boolean;
  onClick?: () => void;
};

export const Button: React.FC<ButtonProps> = ({
  label,
  leftIcon,
  rightIcon,
  loading = false,
  disabled = false,
  onClick,
}) => {
  return (
    <button className={`btn btn-primary ${loading ? "disabled" : ""}`} onClick={onClick} disabled={loading || disabled}>
      {loading && <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true" />}
      {leftIcon}
      {label}
      {rightIcon}
    </button>
  );
};

export default ReusableButton;
