// import React, { ReactNode } from 'react';

import React, { CSSProperties, ReactNode } from "react";
interface Props {
  children: ReactNode;
}
// export const Box = ({ children }: Props) => {
//   return <div className="container-fluid">{children}</div>;
// };


interface BoxProps {
  children: ReactNode;
  margin?: string;
  padding?: string;
  horizontalArrangement?: "start" | "center" | "end" | "space-between" | "space-around";
  verticalArrangement?: "start" | "center" | "end";
  fullWidth?: boolean;
  fullHeight?: boolean;
  style?: CSSProperties;
}

export const Box: React.FC<BoxProps> = ({
  children,
  margin = "0",
  padding = "0",
  horizontalArrangement = "start",
  verticalArrangement = "start",
  fullWidth = false,
  fullHeight = false,
  style = {},
}) => {
  const outerContainerStyles: CSSProperties = {
    display: "flex",
    flexDirection: "column",
    margin,
    padding,
    width: fullWidth ? "100%" : "auto",
    height: fullHeight ? "100vh" : "auto",
    ...style,
  };
  const innerContainerStyles: CSSProperties = {
    display: "flex",
    flexDirection: "column",
    alignItems: horizontalArrangement,
    justifyContent: verticalArrangement,
    margin,
    padding,
    width: fullWidth ? "100%" : "auto",
    height: fullHeight ? "100vh" : "auto",
    ...style,
  };

  return (
    <div style={containerStyles}>
      <div>{children}</div>
    </div>
  );
};
