import React, { ReactNode } from "react";
interface Spread {
  vertically: "full-height";
  horizontaly: "full-width";
  auto: "auto";
}
interface ContentBoxProps extends Spread {
  children: ReactNode;
  horizontalAlign?: "start" | "center" | "end";
  verticalAlign?: "top" | "middle" | "bottom";
  spread?: "full-width" | "full-height" | "auto";
  margin?: string | number | undefined;
  padding?: string | number ;
}

export const ContentBox: React.FC<ContentBoxProps> = ({
  children,
  horizontalAlign = "start",
  verticalAlign = "top",
  spread = "auto",
  margin = "16",
  padding = "8",
}) => {
  const containerStyle: React.CSSProperties = {
    display: "flex",
    flexDirection: spread === "full-width" ? "column" : "row",
    justifyContent: horizontalAlign === "start" ? "flex-start" : horizontalAlign === "center" ? "center" : "flex-end",
    alignItems: verticalAlign === "top" ? "flex-start" : verticalAlign === "middle" ? "center" : "flex-end",
    margin: margin,
    padding: padding,
    width: spread === "full-width" ? "100%" : "auto",
    height: spread === "full-height" ? "100%" : "auto",
  };

  return (
    <div className="container" style={containerStyle}>
      {children}
    </div>
  );
};
