import React from "react";

type ButtonProps = {
  label?: string;
  icon?: React.ReactNode;
  leftIcon?: React.ReactNode;
  rightIcon?: React.ReactNode;
  isLoading?: boolean;
  disabled?: boolean;
  onClick?: () => void;
  style?: React.CSSProperties;
};

export const Button: React.FC<ButtonProps> = ({
  icon,
  label,
  leftIcon,
  rightIcon,
  isLoading = false,
  disabled = false,
  onClick,
  style,
}) => {
  const buttonStyle: React.CSSProperties = {
    color: "white",
    fontWeight: "bold",
    ...style,
  };
  return (
    <button
      className={`d-flex btn btn-primary color-white ${isLoading ? "disabled" : ""}`}
      style={buttonStyle}
      onClick={!isLoading && !disabled ? onClick : undefined}>
      {icon ? (
        <span className="m-1">{icon}</span>
      ) : (
        <div>
          {leftIcon && <span className="me-2">{leftIcon}</span>}
          {isLoading && (
            <div className="spinner-border" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          )}
          {label && <div>{label}</div>}
          {rightIcon && <span className="ms-2">{rightIcon}</span>}
        </div>
      )}
    </button>
  );
};
