import React from "react";

type ButtonProps = {
  label?: string;
  icon?: React.ReactNode;
  leftIcon?: React.ReactNode;
  rightIcon?: React.ReactNode;
  isLoading?: boolean;
  disabled?: boolean;
  onClick?: () => void;
};

export const Button: React.FC<ButtonProps> = ({
    icon,
  label,
  leftIcon,
  rightIcon,
  isLoading = false,
  disabled = false,
  onClick,
}) => {
  return (
    <button
      className={`d-flex btn btn-primary ${isLoading ? "disabled" : ""}`}
          onClick={!isLoading && !disabled ? onClick : undefined}>
          {icon ? <span className="m-2">{ icon}</span> : <div></div>
     }
    </button>
  );
};
