import React, { CSSProperties, ReactNode } from "react";

interface ContainerProps {
  children: ReactNode;
  margin?: string;
  padding?: string;
  horizontalArrangement?: "start" | "center" | "end" | "space-between" | "space-around";
  verticalArrangement?: "start" | "center" | "end";
  fullWidth?: boolean;
  fullHeight?: boolean;
  style?: CSSProperties;
}

export const ContentBox: React.FC<ContainerProps> = (props) => {
  const {
    children,
    margin = "16",
    padding = "8",
    horizontalArrangement = "start",
    verticalArrangement = "start",
    fullWidth = false,
    fullHeight = false,
    style = {},
  } = props;
  console.log(props)

  const outerContainerStyles: CSSProperties = {
    display: "flex",
    flexDirection: horizontalArrangement ? "row" : "column",
    margin: margin ? margin : "16",
    padding,
  };
  const innerContainerStyles: CSSProperties = {
    display: "flex",
    flexDirection: "column",
    alignItems: horizontalArrangement,
    justifyContent: verticalArrangement,
    // margin,
    // padding,
    width: fullWidth ? "100%" : "auto",
    height: fullHeight ? "100vh" : "auto",
    ...style,
  };
  // const innerContainerStyles: CSSProperties = {
  //   display: "flex",
