  //   margin,
  //   padding,
  //   width: fullWidth ? "100%" : "auto",
  //   height: fullHeight ? "100vh" : "auto",
  //   ...style,
  // };
console.log(outerContainerStyles)
  return (
    <div style={outerContainerStyles}>
      <div style={innerContainerStyles}>{children}</div>
    </div>
  );
};
